tinymce.PluginManager.add("CropImage",function(e,t){
	e.addButton("CropImage",{
		text:"My button",icon:!1,
		onclick:function(){
			e.windowManager.open({
				title:"CropImage plugin",
				body:[
					{type:"textbox",name:"title",label:"Title"}
				],
				onsubmit:function(t){
					e.insertContent("Title: "+t.data.title)
				}
			})
		}
	}),
	e.addMenuItem("CropImage",{
		text:"CropImage plugin",
		context:"tools",
		onclick:function(){
			e.windowManager.open({
				title:"TinyMCE site",
				url:t+"/dialog.html",
				width:1024,
				height:600,
				buttons:[
					{
						text:"Insert",
						onclick:function()
						{
							var t=e.windowManager.getWindows()[0];


							var url = t.getContentWindow().document.getElementById('sample_input').value;
							
							tinymce.activeEditor.insertContent('<img alt="Smiley face"  src="'+url+'"/>');
							

		
							
							t.close();
						}
					},
							{text:"Close",onclick:"close"}
				]
				
			})
		}
	})
});